import React, { useState } from "react";

const App = () => {
    const time = new Date().toLocaleTimeString();
    const [curTime, setTime] = useState(time);
    const upTime = () => {
        const time = new Date().toLocaleTimeString();
        setTime(time);
    }
    
    return(
        <>
            <h1>{curTime}</h1>
            <button onClick={upTime}>Get Time</button>
        </>
    )
}

export default App;
