import React, { useState } from "react";

const App = () => {
    const bgColor = "#800080";
    const [bgChange, setBg] = useState(bgColor);

    const bgClick = () =>{
        const newBg = "#445e94";
        setBg(newBg);
    }
    
    return(
        <>
            <div className="click" style={{bgColor}}>
                <button onClick={bgClick}>Click Me</button>
            </div>
        </>
    )
}

export default App;
