import React, { useState } from "react";

const App = () => {
    const bgWhite = "#000";
    const [white, setColor] = useState(bgWhite);
    const nightMood = "ðŸ–¤";
    const [night, setDay] = useState(nightMood)

    const black = () =>{
        const day = "#fff";
        setColor(day);
        const nightDay = "";
        setDay(nightDay);
    }
    return(
        <>
            <div className="click" style={{backgroundColor: white}}>
                <button onClick={black}>{night}</button>
            </div>
        </>
    )
}

export default App;
