const App = () => {
    const bgColor = "#800080";
    const [bgChange, setBg] = useState(bgColor);
    const name = "Click Me";
    const [newName, setName] = useState(name);

    const bgClick = () =>{
        const newBg = "#445e94";
        setBg(newBg);
        const cngName = "Yahhuuu ðŸ¤¯"
        setName(cngName)
    }
    
    return(
        <>
            <div className="click" style={{backgroundColor: bgChange}}>
                <button onClick={bgClick}>{name}</button>
            </div>
        </>
    )
}

export default App;
