import React, { useState } from "react";
const App = () =>{
    const num = "0"
    const [number, setNumber] = useState(num);
    const inc =() => {
        setNumber(number+1)
    }
    const dec =() => {
        setNumber(() => {
            if(number>0){

                return number - 1;
            }
            else{
                alert("Not any Decrement")
            }
        })
    }
    return (
        <>
            <div className="click">
                <div className="box">
                    <h1>{number}</h1>
                    <br/>
                    <button onClick={dec}>decrement</button>
                    <button onClick={inc}>increment</button>
                </div>
            </div>
        </>
    )
}
export default App;
