import React, { useState } from "react";





const App = () => {

    const times = new Date().toLocaleTimeString();
    
    const [time, setTime] = useState(times)
    const upTime = () => {
        const times = new Date().toLocaleTimeString();
        setTime(times)
    }
    return(
        <>
            <h1>{time}</h1>
            <button onClick={upTime}> Get Time </button> 
        </>
    )
}

export default App;
