import React, { useState } from "react";

const App = () => {
    const [fullName, setName] = useState({
        fname: "",
        lname: "",
        email: "",
        number: ""
    })
    const inputEvent = (event) =>{
        console.log(event.target.value);
        console.log(event.target.name);

        let value = event.target.value;
        let name = event.target.name;

        setName((preValue) =>{
            if(name === "fname"){
                return{
                    fname: value,
                    lname: preValue.lname,
                    email: preValue.email,
                    number: preValue.number
                }
            }
            else if(name === "lname"){
                return{
                    fname: preValue.fname,
                    lname: value,
                    email: preValue.email,
                    number: preValue.number
                }
            }
            else if(name === "email"){
                return{
                    fname: preValue.fname,
                    lname: preValue.lname,
                    email: value,
                    number: preValue.number
                }
            }
            else if(name === "number"){
                return{
                    fname: preValue.fname,
                    lname: preValue.lname,
                    email: preValue.email,
                    number: value
                }
            }
        })
    }

    const sub = (event) => {
        event.preventDefault();
        alert("Form Submited")
    }
    return(
        <>
            <div className="click">
                <form onSubmit={sub}>
                    <h1>Hello {fullName.fname} {fullName.lname}</h1>
                    <p>{fullName.email}</p>
                    <p>{fullName.number}</p>
                    <input type="text" placeholder="Enter Your First Name" name="fname" value={fullName.fname} onChange={inputEvent}/>
                    <br />
                    <input type="text" placeholder="Enter Your Second Name" name="lname" value={fullName.lname} onChange={inputEvent}/>
                    <br />
                    <input type="email" placeholder="Enter Your Email Name" name="email" value={fullName.email} onChange={inputEvent}/>
                    <br />
                    <input type="number" placeholder="Enter Your Mobile Number" name="number" value={fullName.number} onChange={inputEvent}/>
                    <button type="submit">Submit Here</button>
                </form>
            </div>
        </>
    )
}

export default App;
