import React, { useState } from "react";
const CountApp = () =>{
    const [number, setNumber] = useState(0);
    const inc =() => {
        setNumber(number+1)
    }
    const dec =() => {
        if(number>0){
            return setNumber(number - 1)
        }
        else{
            alert("Not Allowed")
        }
    }
    return (
        <>
            <div className="click">
                <div className="box">
                    <h1>{number}</h1>
                    <br/>
                    <button onClick={dec}>decrement</button>
                    <button onClick={inc}>
                        <i className="fa fa-plus"></i>
                    </button>
                </div>
            </div>
        </>
    )
}
export default CountApp;
