import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

let greetings = "";
let greetime = new Date();
greetime = greetime.getHours();
let time = new Date().toLocaleDateString();
let date = new Date().toLocaleTimeString();
let cssStyle = {};
if (greetime>=1 && greetime<12){
    greetings = "Morning"
    cssStyle.color = "Black";
}
else if(greetime>=12 && greetime<19){
    cssStyle.color = "green";
    greetings = "Afternoon";
}
else{
    cssStyle.color = "orange";
    greetings = "Night";
}
ReactDOM.render(
    <>
        <h1 className="heading">Good <span style={cssStyle}>{greetings}</span> </h1>
        <p style={{color:"#fff"}}>{time} <span>{date}</span></p>
    </>,
    document.getElementById("root")
    );
