import React, { useState } from "react";

const App = () => {
    const [fullName, setName] = useState({
        fname: "",
        lname: ""
    })
    const inputEvent = (event) => {
        console.log(event.target.value)
        console.log(event.target.name)

        let value = event.target.value
        let name = event.target.name

        setName((preValue) => {
            if(name === "fname"){
                return{
                    fname: value,
                    lname: preValue.lname
                }
            } else if(name === "lname"){
                return{
                    fname: preValue.fname,
                    lname: value
                }
            }
        })
    }
    const sub = (event) => {
        event.preventDefault();
        alert("Submited")
    }

    return(
        <>
            <div className="click">
                <form onSubmit={sub}>
                <h1>Hello {fullName.fname} {fullName.lname}</h1>
                <input type="text" placeholder="Enter Your First Name" onChange={inputEvent} value={fullName.fname} name="fname"/>
                <br/>
                <input type="text" placeholder="Enter Your Second Name" onChange={inputEvent} value={fullName.lname} name="lname"/>
                <button type="submit">Click Me üëç</button>
                </form>
            </div>
        </>
    )
}

export default App;
