import React, { useState } from "react";

const App = () => {
    const [counter, setCount] = useState(0);
    const inc = () => {
        setCount(counter + 1)
    }
    const dec = () => {
        if(counter>0){
            setCount(counter -1);
        }
        else{
            alert("This is not anymore Decrement")
        }
    }
    
    return(
        <>
            <div className="click">
                <button>
                    <span onClick={inc}>+</span> &nbsp; &nbsp;
                    <h1>{counter}</h1>
                    <span onClick={dec}>-</span>
                </button>
            </div>
        </>
    )
}

export default App;
