import React, { useState } from "react";

const App = () => {
    const bgColor = "#800080";
    const [bgChange, setBg] = useState(bgColor);
    const name = "Click Me";
    const [newName, setName] = useState(name);

    const bgClick = () =>{
        const newBg = "#445e94";
        setBg(newBg);
        setName("Yahhuuu ðŸ¤¯");
    }
    const dbClick = () =>{
        const dbBg = "#645e94";
        setBg(dbBg);
        setName("Another one ðŸ˜ˆ");
    }
    
    return(
        <>
            <div className="click" style={{backgroundColor: bgChange}}>
                <button onClick={bgClick} onDoubleClick={dbClick}>{newName}</button>
            </div>
        </>
    )
}

export default App;
