import React, { useState } from 'react';
import "./header.css";
import Header from "./Header";
import Note from './Note';
import ChildNote from './ChildNote';
import Footer from './Footer';


function KeepApp() {
  const [expand, setExpand] = useState()
  const [addItem, setAddItem] = useState([])
  const addNote = (note) => {
    setAddItem((preData) => {
      return[
        ...preData, note
      ]
    })
    
  }
  const onDelete = (id) => {
    setAddItem((oldData) => {
      oldData.filter((curData,index) =>{
        return index !== id;
      })
    });
  }
  return (
    <>
      <Header />
      <Note passNote = {addNote}/>
      {addItem.map((val, index) => {
          return (
            <ChildNote
            key={index}
            id = {index}
            title ={val.title}
            content = {val.content}
            deleteItem={onDelete}
          />
          )
        })}
      <Footer />

    </>
  )
}

export default KeepApp
