import React, { useState } from "react";

const App = () => {
    const time = new Date().toLocaleTimeString();
    const [curTime, setTime] = useState(time);
    const upDate = () => {
        const time = new Date().toLocaleTimeString();
        setTime(time);
    }

    setInterval(upDate);
    
    return(
        <>
            <div className="click">
                <h1>{curTime}</h1>
                <button>Get Time</button>
            </div>
        </>
    )
}

export default App;
