import React, { useState } from "react";
import AddIcon from '@mui/icons-material/Add';
import RemoveCircleIcon from '@mui/icons-material/RemoveCircle';

const CountApp = () =>{
    const [number, setNumber] = useState(0);
    const inc =() => {
        setNumber(number+1)
    }
    const dec =() => {
        if(number>0){
            return setNumber(number - 1)
        }
        else{
            alert("Not Allowed")
        }
    }
    return (
        <>
            <div className="click">
                <div className="box">
                    <h1>{number}</h1>
                    <br/>
                    <button onClick={dec}><RemoveCircleIcon style={{backgroundColor: "red"}}/></button>
                    <button onClick={inc}>
                    <AddIcon />
                    </button>
                </div>
            </div>
        </>
    )
}
export default CountApp;
