import React, { useState } from "react";
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';

const Note = () => {
    const [note, setNote] = useState({
        title: '',
        content: ''
    });
    const inputEvent = (event) => {
        const [name , value] = event.target;
        setNote((preValue) => {
            return{
                ...preValue
            }
        })
    }
    return(
        <>
            <div className="note">
                <form>
                    <input type="text" value={note.title} placeholder="Title" autoComplete="off" name="title" onChange={inputEvent}/>
                    <textarea value={note.content} placeholder="Enter your Text" name="content" onChange={textEvent}></textarea>
                    <Button className="notebtn" >
                        <AddIcon />
                    </Button>
                </form>
            </div>
        </>
    )
}

export default Note;
