import React from 'react';
import "./img.css";
import Card from './Card';
import ImgData from "./ImgData";

const Galery = () => {
    const filterItem = (categoryItem) => {
        ImgData.filter((curElement) => {
            return curElement.category == categoryItem
        })
        console.log(ImgData);
    }
    return (
        <>
            <h1 className='mt-5 text-center'>Order Our Favourite Dish</h1>
            <hr />
            <div className="container">
                <div className="d-flex justify-content-around">
                    <button className='btn btn-warning' >All</button>
                    <button className='btn btn-warning' onClick={() => filterItem("breakfast")}>Breakfast</button>
                    <button className='btn btn-warning'>Lunch</button>
                    <button className='btn btn-warning'>Evening</button>
                    <button className='btn btn-warning'>Dinner</button>
                </div>
            </div>
            <div className="container-fluid">
                <div className="row">
                    <div className="col-11 mx-auto">
                        <div className="row my-5">
                            {
                                ImgData.map((val, ind) => {
                                    return <Card
                                        key={ind}
                                        img={val.img}
                                        title={val.title}
                                        price={val.price}
                                        dec={val.dec} />
                                })
                            }

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Galery
