import React, { useState } from "react";

const App = () => {
    const [item, setItem] = useState({
        product:""
    })
    const [list, setList] = useState([]);
    const inputEvent = (event) => {
        console.log(event.target.value)
        const {name, value} = event.target;

        setItem((preValue) =>{
            return{
                ...preValue,
                [name] : value
            }
        })
    }
    const sub = (event) => {
        event.preventDefault();
    }
    return(
        <>
            <div className="click">
                <div className="box">
                    <br />
                    <h1> ToDo List </h1>
                    <br />
                    <input type="text" placeholder="Add a Items" onChange={inputEvent} name="product" value={item.product}/>
                    <button onClick={sub}>+</button>
                    <br />
                    <ul>
                        {list.map((itemValue) => {
                            return <li>{itemValue}</li>
                        })}
                    </ul>
                </div>
            </div>
        </>
    )
}

export default App;
